function titleCase(str) {

var strArray;

strArray = str.toLowerCase().split("");
strArray[0] = strArray[0].toUpperCase();

var i=0;

while (i < strArray.length) {
if (strArray[i] === " " && strArray[i+1] !== " ") {
strArray[i+1] = strArray[i+1].toUpperCase();
}
i++;
}

return strArray.join("");
}

